package TodoModelServices;import com.google.appengine.api.datastore.EntityNotFoundException;import com.google.appengine.api.datastore.Key;import com.google.appengine.tools.development.testing.LocalDatastoreServiceTestConfig;import com.google.appengine.tools.development.testing.LocalServiceTestHelper;import org.junit.AfterClass;import static org.junit.Assert.assertEquals;import org.junit.BeforeClass;import org.junit.Test;public class TodoDataServicesTest {    private static final LocalServiceTestHelper helper =            new LocalServiceTestHelper(                    new LocalDatastoreServiceTestConfig()                            .setDefaultHighRepJobPolicyUnappliedJobPercentage(0));    private static final TodoDataServices dataServices = new TodoDataServices();    static  Key key = null;    @BeforeClass    public static void  setUpClass() {        helper.setUp();          key = dataServices.saveTask("daily","dailyKey");    }    @AfterClass    public static void tearDown() {        helper.tearDown();    }  @Test    public void saveTask_valid_type (){      assertEquals("dailyKey",key.getName());  }    @Test    public void get_valid_type () throws EntityNotFoundException {        assertEquals("daily",dataServices.getTask(key.getName()).getProperty("type"));    }}